mapper.select:
select * from (select rownum rnum, member_name, member_gender, member_id, member_pw, member_email, member_zip, member_address1, member_address2, member_phone, to_char(to_date(member_birthday),'YYYY-MM-DD') as member_birthday, grade_code_name, member_height, member_weight, member_bmi, shape_code, fat_part1, fat_part2, fat_part3, fat_part4, fat_part5, fat_part6, activity_code1, activity_code2, activity_code3, activity_code4, activity_code5, member_kcal, coupons_no, member_point, member_state, to_char(to_date(member_join_date),'YYYY-MM-DD') as member_join_date, member_visit_count, to_char(to_date(member_last_visit_date),'YYYY-MM-DD') as member_last_visit_date from (select * from member where member_state = 1 order by member_join_date desc, member_name asc)) where rnum >= #{startRow} and rnum <= #{endRow}
and member_id like '%${id}%'
and member_name like '%${name}%'
and member_gender = #{gender}
and grade_code_name = #{grade}
and member_join_date = to_date(#{join_date})
and member_last_visit_date = to_date(#{last_visit_date})

mapper.update:
update member set
	grade_code_name = #{grade},
	member_point = #{point},
	member_state = #{state}
where member_id=#{id}
